mapper.select:
/* Board - boardList */
select b_num, b_name, b_title, to_char(b_date, 'YYYY-MM-DD') as b_date 
from spring_board

mapper.insert:
/* Board - insertBoard */
select spring_board_seq.nextval from dual
INSERT INTO spring_board (b_num, b_name, b_title, b_content, b_pwd) VALUES (#{b_num}, #{b_name}, #{b_title}, #{b_content}, #{b_pwd})

mapper.select:
/* Board - boardDetail */
SELECT b_num, b_name, b_title, b_content, TO_CHAR(b_date, 'YYYY-MM-DD HH24:MI:SS') AS b_date
FROM	   spring_board
WHERE b_num = #{b_num}
